package week1.day2;

public class Findoddnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int maxrange=12;
		for (int i = 1; i <= maxrange; i++) {
			if (i%2==1) {
				
				System.out.println("Odd number: " +i);
			}
			else 
				System.out.println("Even Number: "+i);
			
		}

	}

}

